mapper.select:
SELECT COMM
FROM COMM
WHERE F_IDX = #{f_idx}

SELECT COUNT(L_IDX)
  FROM LIKE_LIST
 WHERE U_ID = #{u_id}
   AND F_IDX = #{f_idx}

mapper.delete:
DELETE FROM LIKE_LIST
 WHERE F_IDX = #{f_idx}
   AND U_ID = #{u_id}

mapper.select:
		SELECT COUNT(F_IDX)
		  FROM LIKE_LIST
         WHERE F_IDX = #{f_idx}
