mapper.delete:
DELETE FROM FOLLOW
WHERE TO_ID = #{u_id}
OR FROM_ID = #{u_id}

delete  from USER_INFO 
where U_ID = #{u_id}
AND PWD = #{delkeyword}

mapper.select:
SELECT COUNT(U_ID)
FROM USER_INFO
WHERE U_ID = #{u_id}

SELECT U_ID
  FROM USER_INFO
 WHERE EMAIL = #{email}
   AND PHONE = #{phone}
   AND BIRTH = #{birth}

SELECT PWD
  FROM USER_INFO
 WHERE U_ID = #{u_id}
   AND EMAIL = #{email}
   AND PHONE = #{phone}
   AND BIRTH = #{birth}
